row.script:
function main() {
    // Dome
    var spine;

    if (params.width < 2)
        params.width = 2;

    if (params.length < 15)
        params.length a= 15;

    if (params.width/params.length < 0.2)
        params.width = 0.2 * params.length;

    spine = cylinder({
        r: params.length / 2, 
        h: params.height
    }).translate([0, params.length / 2, 0]).scale([0.3, 1, 1]);

    return difference(
        spine,
        cube({size: [params.width, params.length, params.height]})
    );
}

g.script:
function main() {
    // Teeth
    var teeth = []
    var dist = 2.75, n = params.length
    var div = (params.length - 5) / params.length
    for (var i = 0; i < n; i++) {
        teeth.push('')
        dist = dist + div
    }
    return teeth.join('\n')
}
