row.script:
function main() {
    // Dome
    var spine;

    if (params.width < 2)
        params.width = 2;

    if (params.length < 15)
        params.length = 15;

    if (params.width/params.length < 0.2)
        params.width = 0.2 * params.length;

    spine = cylinder({
        r: params.length / 2, 
        h: params.height
    }).translate([0, params.length / 2, 0]).scale([0.3, 1, 1]);

    return difference(
        spine,
        cube({size: [params.width, params.length, params.height]})
    );
}
